import React, { useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, ScatterChart, Scatter, PieChart, Pie, Cell } from 'recharts';

const WineQualityDashboard = () => {
  const [selectedView, setSelectedView] = useState('story');
  const [currentStoryStep, setCurrentStoryStep] = useState(1);

  // Inline styles for professional appearance
  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #fef7f7 0%, #ffffff 50%, #fffbf0 100%)',
      padding: '24px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '48px'
    },
    mainTitle: {
      fontSize: '3.5rem',
      fontWeight: 'bold',
      color: '#8B0000',
      marginBottom: '16px',
      textShadow: '2px 2px 4px rgba(0,0,0,0.1)'
    },
    subtitle: {
      fontSize: '1.5rem',
      color: '#6b7280',
      marginBottom: '8px'
    },
    dataInfo: {
      fontSize: '1.1rem',
      color: '#9ca3af'
    },
    navigation: {
      display: 'flex',
      justifyContent: 'center',
      marginBottom: '48px'
    },
    navContainer: {
      backgroundColor: 'white',
      borderRadius: '12px',
      boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
      padding: '8px',
      display: 'flex',
      gap: '8px'
    },
    navButton: {
      padding: '12px 24px',
      borderRadius: '8px',
      border: 'none',
      fontWeight: '600',
      fontSize: '16px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      background: 'transparent',
      color: '#6b7280'
    },
    navButtonActive: {
      background: 'linear-gradient(135deg, #dc2626, #b91c1c)',
      color: 'white',
      boxShadow: '0 4px 12px rgba(220, 38, 38, 0.3)',
      transform: 'scale(1.05)'
    },
    storyNavigation: {
      display: 'flex',
      justifyContent: 'center',
      gap: '16px',
      marginBottom: '32px',
      flexWrap: 'wrap'
    },
    storyButton: {
      padding: '12px 24px',
      borderRadius: '12px',
      border: 'none',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      fontSize: '14px'
    },
    storyButtonActive: {
      background: 'linear-gradient(135deg, #dc2626, #b91c1c)',
      color: 'white',
      boxShadow: '0 6px 16px rgba(220, 38, 38, 0.4)',
      transform: 'scale(1.05)'
    },
    storyButtonInactive: {
      backgroundColor: '#f3f4f6',
      color: '#374151'
    },
    storyContainer: {
      background: 'linear-gradient(135deg, #fef2f2 0%, #fef3c7 100%)',
      padding: '48px',
      borderRadius: '16px',
      boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
      marginBottom: '32px'
    },
    storyTitle: {
      fontSize: '2.5rem',
      fontWeight: 'bold',
      color: '#8B0000',
      marginBottom: '16px',
      textAlign: 'center'
    },
    storySubtitle: {
      fontSize: '1.5rem',
      color: '#dc2626',
      marginBottom: '24px',
      textAlign: 'center'
    },
    storyContent: {
      fontSize: '1.2rem',
      color: '#374151',
      marginBottom: '24px',
      textAlign: 'center',
      lineHeight: '1.6',
      maxWidth: '800px',
      margin: '0 auto 24px'
    },
    statsBox: {
      backgroundColor: '#fef3c7',
      padding: '16px',
      borderRadius: '12px',
      display: 'inline-block',
      boxShadow: '0 4px 12px rgba(251, 191, 36, 0.3)'
    },
    statsText: {
      fontWeight: 'bold',
      color: '#92400e',
      fontSize: '1.1rem'
    },
    metricsGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
      gap: '24px',
      marginBottom: '48px'
    },
    metricCard: {
      background: 'linear-gradient(135deg, #dc2626, #b91c1c)',
      color: 'white',
      padding: '32px',
      borderRadius: '16px',
      boxShadow: '0 8px 24px rgba(220, 38, 38, 0.3)',
      textAlign: 'center'
    },
    metricValue: {
      fontSize: '2.5rem',
      fontWeight: 'bold',
      marginBottom: '8px'
    },
    metricLabel: {
      fontSize: '1rem',
      opacity: 0.9,
      marginBottom: '4px'
    },
    metricDetail: {
