import React, { useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, ScatterChart, Scatter, PieChart, Pie, Cell } from 'recharts';

const WineQualityDashboard = () => {
  const [selectedView, setSelectedView] = useState('story');
  const [currentStoryStep, setCurrentStoryStep] = useState(1);

  // Professional inline styles - no external CSS needed
  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #fef7f7 0%, #ffffff 50%, #fffbf0 100%)',
      padding: '24px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif'
    },

    // Header Styles
    header: {
      textAlign: 'center',
      marginBottom: '48px'
    },
    mainTitle: {
      fontSize: '3.5rem',
      fontWeight: 'bold',
      color: '#8B0000',
      marginBottom: '16px',
      textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
      lineHeight: '1.1'
    },
    subtitle: {
      fontSize: '1.5rem',
      color: '#6b7280',
      marginBottom: '8px'
    },
    dataInfo: {
      fontSize: '1.1rem',
      color: '#9ca3af'
    },

    // Navigation Styles
    navigation: {
      display: 'flex',
      justifyContent: 'center',
      marginBottom: '48px'
    },
    navContainer: {
      backgroundColor: 'white',
      borderRadius: '12px',
      boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
      padding: '8px',
      display: 'flex',
      gap: '8px'
    },
    navButton: {
      padding: '12px 24px',
